import React from 'react';

const CityInput = ({ user, city, setCity }) => {
  return (
    <div className="field">
      <label>Město</label>
      <input
        type="text"
        name="city"
        placeholder={user === null ? 'Město' : user.city}
        id="First name"
        required
        onChange={(e) => setCity(e.target.value)}
      />
      {!isValidCity ? (
        <div className="validation">Vyplňte město</div>
      ) : (
        <div></div>
      )}
    </div>
  );
};

export default CityInput;
