import React from 'react';

const EmailInput = ({ email, setEmail, user }) => {
  const emailValidation = () => {
    if (email === '') {
      return <div></div>;
    }
    if (email.split(' ').length > 1) {
      return <div>Je tam Mezera</div>;
    } else {
      return <div className="validation">Jméno a příjmení jsou 2 slova</div>;
    }
  };
  return (
    <div className="field">
      <label>Email</label>
      <input
        type="text"
        name="email"
        placeholder={user === null ? 'Email' : user.email}
        id="email"
        onChange={(e) => setEmail(e.target.value)}
      />
      {emailValidation()}
    </div>
  );
};

export default EmailInput;
