import { useState, useMemo } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import routes from './routes/Routes';
import { UserContext } from './context/UserContext';
import { CardContext } from './context/CardContext';
import FooterNavBar from './navBar/FooterNavBar';
import TopNavBar from './navBar/TopNavBar';

function App() {
  const [user, setUser] = useState(null);
  const [shopCard, setShopCard] = useState(null);

  //useMemo Create
  const valueUser = useMemo(() => ({ user, setUser }), [user, setUser]);
  const valueCard = useMemo(
    () => ({ shopCard, setShopCard }),
    [shopCard, setShopCard]
  );

  console.log(user);
  console.log(shopCard);

  return (
    <CardContext.Provider value={valueCard}>
      <UserContext.Provider value={valueUser}>
        <BrowserRouter>
          <TopNavBar />
          <Routes>
            {routes.map((route, index) => (
              <Route
                key={index}
                path={route.path}
                exact={route.exact}
                element={<route.component />}
              ></Route>
            ))}
          </Routes>
          <FooterNavBar />
        </BrowserRouter>
      </UserContext.Provider>
    </CardContext.Provider>
  );
}

export default App;
