import React from 'react';

const NameInput = ({ user, name, setName }) => {
  return (
    <div className="field">
      <h3>Objednavatel</h3>
      <label>Jméno a příjmení</label>
      <input
        type="text"
        name="name"
        placeholder={user === null ? 'Jméno a příjmení' : user.name}
        required
        onChange={(e) => setName(e.target.value)}
      />
    </div>
  );
};

export default NameInput;
